INSERT INTO ravoli_db.prestation (prestation_id, prestation_title, prestation_subtext, prestation_price, prestation_duration, prestation_sub_cat, prestation_order, prestation_visible) VALUES (93, 'Regard carita', 'les masques regard carita prennent soin de la zone particulièrement fragile du contour de l’œil grâce à une gestuelle experte et douce et des produits d’exception. ce traitement met à profit les bienfaits de la gamme progressif neomorphose pour transformer la peau : les rides sont lissées, les poches sont gommées et les signes de l’âge et les marques de fatigue s’estompent. grâce à ce soin ciblé contour de l’œil le regard s''ouvre et retrouve tout son éclat, comme pétillant de jeunesse. option à ajouter à votre soin visage.', '14', '15', 17, 0, 1);
INSERT INTO ravoli_db.prestation (prestation_id, prestation_title, prestation_subtext, prestation_price, prestation_duration, prestation_sub_cat, prestation_order, prestation_visible) VALUES (94, 'Soin my cle 30 min', 'la cosmétique instrumentale, une nouvelle ère du soin pour repousser les limites de votre routine beauté. découvrez my c.l.e., le 1er appareil beauté nomade carita qui bouscule les codes esthétiques et vous transporte dans une expérience de soin inédite. 1 appareil – 2 énergies pour une expérience personnalisée.', '39', '30', 17, 0, 1);
INSERT INTO ravoli_db.prestation (prestation_id, prestation_title, prestation_subtext, prestation_price, prestation_duration, prestation_sub_cat, prestation_order, prestation_visible) VALUES (95, 'Purete controle', 'pensé pour ré-équilibrer les peaux mixtes à grasses, le grand soin pureté controle travaille en douceur la surface de la peau pour déverrouiller les voies de détoxification. ce véritable rituel d’hydratation rééquilibrant visage aide la peau à respirer et à retrouver sérénité et régularité. durablement matifié et purifié, le visage est comme enveloppé d’un délicieux voile de fraîcheur et de bien-être. le teint s’unifie et s’illumine tandis que la texture de la peau retrouve souplesse et densité.', '59', '60', 17, 0, 1);
INSERT INTO ravoli_db.prestation (prestation_id, prestation_title, prestation_subtext, prestation_price, prestation_duration, prestation_sub_cat, prestation_order, prestation_visible) VALUES (96, 'Hydratation lagons ', 'puisée au cœur de la polynésie, l''eau des lagons libère tous les bienfaits de sa minéralité exceptionnelle dans ce rituel d''hydratation profonde. véritable bain de fraîcheur et de bien-être, le grand soin visage hydratation des lagons revitalise les peaux ternes et fatiguées et les laisse ressourcées, intensément hydratées. les réservoirs en eau se rechargent, la peau se gorgent de fraîcheur et le teint se réveille et s''illumine, habité d''une énergie nouvelle.', '59', '60', 17, 0, 1);
INSERT INTO ravoli_db.prestation (prestation_id, prestation_title, prestation_subtext, prestation_price, prestation_duration, prestation_sub_cat, prestation_order, prestation_visible) VALUES (97, 'Douceur sensidote', 'pour réconforter les peaux les plus sensibles, carita invente le grand soin douceur sensidote. grâce aux mouvements délicats mais techniques de l’experte de beauté carita, la peau s’apaise immédiatement et retrouve tout son confort. véritable cocon de bien-être et sérénité, ce rituel d’hydratation apaisante visage se vit comme une caresse au cours de laquelle les rougeurs s’estompent, les tiraillements disparaissent et le teint s’homogénéise. la peau se révèle, plus forte, souple et douce, pour un teint velouté et éclatant.', '79', '60', 17, 0, 1);
INSERT INTO ravoli_db.prestation (prestation_id, prestation_title, prestation_subtext, prestation_price, prestation_duration, prestation_sub_cat, prestation_order, prestation_visible) VALUES (98, 'Soin stimulift ', 'modelage puissant et dynamique des muscles du visage, le grand soin stimulift redessine les contours, sculpte les volumes et rehausse le regard. les actifs des différents produits d’exception utilisés dans ce soin visage lift fermeté infusent sous les mains de l’experte carita et agissent au cœur de la peau pour initier une transformation de peau unique. grâce aux outils de beauté de haute technologie, le teint s’homogénéise et se révèle velouté et lumineux. les contours du visage se redéfinissent et les peaux en manque d’élasticité retrouvent fermeté et rebond.', '79', '90', 17, 0, 1);
INSERT INTO ravoli_db.prestation (prestation_id, prestation_title, prestation_subtext, prestation_price, prestation_duration, prestation_sub_cat, prestation_order, prestation_visible) VALUES (99, 'Soin neomorphose', 'grâce à ses trois actions complémentaires – resurfaçante, repulpante et régénérante – ce soin ultra concentré à l’approche multidimensionnelle réinvente l’éclat et la texture de la peau. premier traitement professionnel hautement transformatif, le soin visage anti-âge neomorphose lisse les irrégularités de micro-relief et laisse la peau lisse, douce et veloutée. sous forme de cure en plusieurs étapes entièrement personnalisables, ce rituel de jouvence s’adapte parfaitement aux besoins de chaque peau pour leur redonner uniformité et lumière.', '119', '90', 17, 0, 1);
INSERT INTO ravoli_db.prestation (prestation_id, prestation_title, prestation_subtext, prestation_price, prestation_duration, prestation_sub_cat, prestation_order, prestation_visible) VALUES (100, 'Soin parfait 3 ors', 'le grand soin parfait 3ors est pensé pour travailler chaque zone et chaque ligne du visage comme autant de joyaux. des modelages relaxants et drainants, effectués avec la précision d’un orfèvre, font de ce soin cabine une expérience lumineuse, transformative et hors du commun. l''experte de beauté carita, grâce à son savoir-faire unique et aux outils de beauté, sculpte les contours du visage en lissant et en raffermissant la peau. la peau est défatiguée et débarrassée de ses impuretés, son éclat se révèle. régénéré et gorgé de lumière, le teint est étincellant de beauté.', '119', '90', 17, 0, 1);
